props.code:
const repository = await Facades.item
    .createRepositoryFromWorkspace(Workspace, 'todos')

const items = await repository.list()

const done = items.filter(i => i.done)

setResult({
    type: 'pie',
    data: {
        labels: ['Done', 'Todo'],
        datasets: [
            {
                label: 'Dataset',
                data: [done.length, items.length - done.length],
                backgroundColor: [
                    'rgb(75, 192, 192)',
                    'rgb(54, 162, 235)'
                ]
            }
        ]
    },
})

